function getAllElementsWithAttribute(e){var t=[];var n=document.getElementsByTagName("*");for(var r=0;r<n.length;r++){if(n[r].getAttribute(e)){t.push(n[r])}}return t}function setBackgroundImage(e,t){e.style.backgroundImage="url("+e.getAttribute(t)+")"}var attribute="data-src";var croplessElements=getAllElementsWithAttribute(attribute);for(var i=0;i<croplessElements.length;i++){setBackgroundImage(croplessElements[i],attribute)}